function Laptops({ searchText = "", sort = "" }) {
  const laptops = [
    {
      id: 1,
      item: "Apple MacBook Pro 14 Inch Space Grey",
      money: 65000,
      url: "https://cdn.dummyjson.com/product-images/laptops/apple-macbook-pro-14-inch-space-grey/thumbnail.webp",
    },
    {
      id: 2,
      item: "Asus Zenbook Pro Dual Screen Laptop",
      money: 34000,
      url: "https://cdn.dummyjson.com/product-images/laptops/asus-zenbook-pro-dual-screen-laptop/thumbnail.webp",
    },
    {
      id: 3,
      item: "Huawei Matebook X Pro",
      money: 39000,
      url: "https://cdn.dummyjson.com/product-images/laptops/huawei-matebook-x-pro/thumbnail.webp",
    },
    {
      id: 4,
      item: "Lenovo Yoga 920",
      money: 55000,
      url: "https://cdn.dummyjson.com/product-images/laptops/lenovo-yoga-920/thumbnail.webp",
    },
    {
      id: 5,
      item: "DELL XPS 13 9300 Laptop",
      money: 45000,
      url: "https://cdn.dummyjson.com/product-images/laptops/new-dell-xps-13-9300-laptop/thumbnail.webp",
    },
    {
      id: 6,
      item: "HP Spectre x360",
      money: 65000,
      url: "data:image/jpeg;base64,",
    },
    {
      id: 7,
      item: "Acer Aspire 5 Slim Laptop",
      money: 25000,
      url: "data:image/jpeg;base64,",
    },
    {
      id: 8,
      item: "Microsoft Surface Laptop 5",
      money: 75000,
      url: "data:image/jpeg;base64,",
    },
    {
      id: 9,
      item: "Razer Blade 15 Gaming Laptop",
      money: 47000,
      url: "data:image/jpeg;base64,",
    },
    {
      id: 10,
      item: "MSI GF63 Thin Gaming Laptop",
      money: 67000,
      url: "data:image/jpeg;base64,",
    },
  ];
  const filteredLaptops = laptops.filter((product) =>
    product.item.toLowerCase().includes(searchText.toLowerCase())
  );
  const sortedLaptops = [...filteredLaptops].sort((a, b) => {
    if (sort === "asc") return a.money - b.money;
    if (sort === "desc") return b.money - a.money;
    return 0;
  });
  const addOrder = (product) => {
    const orderItem = {
      name: product.item,
      price: product.money,
      image: product.url,
    };
    const existingCart = JSON.parse(localStorage.getItem("cart")) || [];
    existingCart.push(orderItem);
    localStorage.setItem("cart", JSON.stringify(existingCart));
    alert("Added to Cart!");
  };

  return (
    <div className="product-container">
      {sortedLaptops.map((product) => (
        <div className="product-card" key={product.id}>
          <img src={product.url} alt={product.item} className="product-image" />
          <h3 className="product-name">{product.item}</h3>
          <p className="product-price">â‚¹{product.money}</p>
          <button
            type="button"
            className="add-cart-btn"
            onClick={() => addOrder(product)}
          >
            Add to Cart
          </button>
        </div>
      ))}
    </div>
  );
}

export default Laptops;
